import React from "react";
import "../css/Header.scss";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";

import Drawer from "@material-ui/core/Drawer";
import MenuItem from "@material-ui/core/MenuItem";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";

import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import MailIcon from "@material-ui/icons/Mail";

// 메뉴 목록
const MENU: { id: number; name: string; href: string }[] = [
  {
    id: 0,
    name: "HOME",
    href: "#home"
  },
  {
    id: 1,
    name: "ABOUT",
    href: "#about"
  },
  {
    id: 2,
    name: "SKILLS",
    href: "#skills"
  },
  {
    id: 3,
    name: "PROJECTS",
    href: "#projects"
  },
  {
    id: 4,
    name: "CONTACT",
    href: "#contact"
  }
];

const useStyles = makeStyles({
  list: {
    width: 250
  },
  fullList: {
    width: "auto"
  }
});

function Header() {
  const [value, setValue] = React.useState(0);
  const classes = useStyles();
  const [state, setState] = React.useState({
    top: false
  });

  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };

  // 모바일인지 체크
  const filter = "win16|win32|win64|mac";
  let mobileCheck = null;

  if (navigator.platform) {
    if (0 > filter.indexOf(navigator.platform.toLowerCase())) {
      mobileCheck = true;
    } else {
      mobileCheck = false;
    }
  }

  if (mobileCheck) {
  } else {
    return (
      <div id="menu">
        <AppBar position="fixed">
          <Tabs
            value={value}
            onChange={handleChange}
            indicatorColor="secondary"
            textColor="secondary"
            className="nav-background-color"
            centered
          >
            {MENU.map(menu => (
              <Tab label={menu.name} href={menu.href} key={menu.id} />
            ))}
          </Tabs>
        </AppBar>
      </div>
    );
  }

  type DrawerSide = "top";
  const toggleDrawer = (side: DrawerSide, open: boolean) => (
    event: React.KeyboardEvent | React.MouseEvent
  ) => {
    if (
      event.type === "keydown" &&
      ((event as React.KeyboardEvent).key === "Tab" ||
        (event as React.KeyboardEvent).key === "Shift")
    ) {
      return;
    }

    setState({ ...state, [side]: open });
  };

  const fullList = (side: DrawerSide) => (
    <div
      role="presentation"
      onClick={toggleDrawer(side, false)}
      onKeyDown={toggleDrawer(side, false)}
    >
      <List>
        {MENU.map(menu => (
          <a href={menu.href} className="under-display-none">
            <ListItem button key={menu.name}>
              <ListItemText
                primary={menu.name}
                className="align-center font-color-deepgray"
              />
            </ListItem>
          </a>
        ))}
      </List>
      <Divider />
    </div>
  );

  return (
    <div className="align-left">
      <AppBar position="fixed">
        <List className="background-color-white">
          <Button onClick={toggleDrawer("top", true)}>
            <MenuIcon />
          </Button>

          <Drawer
            anchor="top"
            open={state.top}
            onClose={toggleDrawer("top", false)}
          >
            {fullList("top")}
          </Drawer>
        </List>
      </AppBar>
    </div>
  );
}

export default Header;
