interface Props {
    title : string;    
}

class Home extends React.Component<Props> {
    state : Props = {
        title : "   Hello, I’m a JavaScript Developer.   "
    }

    onChange = (e: React.FormEvent<HTMLInputElement>): void => {
        const { value } = e.currentTarget;
        const HOMETEXT = "   Hello, I’m a JavaScript Developer.   ";
        let HOMETEXT_len = HOMETEXT.length;
        let index = 0;
        let TEXTSTATE = true;
        let home_text = "";
        let time = 100;

        let timer = setInterval(() => {
            if(index === HOMETEXT_len){
                TEXTSTATE = false;
            }
            else if(index === -1){
                TEXTSTATE = true;
                index++;
            }

            if(TEXTSTATE){
                home_text += HOMETEXT[index++];
            }
            else{
                home_text = home_text.substr(0, --index);
            }   
            this.setState({
                title: home_text
            });
        }, time)

    }

    render() {
        return (
            <div id="home">
                <span>{this.state.title}</span>
            </div>
        )
    }
}

export default Home;
