const HOMETEXT = "   Hello, I’m a JavaScript Developer.     ";
let HOMETEXT_len = HOMETEXT.length;
let index = 0;
let TEXTSTATE = true;
let home_text = "";
let time = 100;

interface Props {}
interface State {
    title : string;
}

class Home extends React.Component<Props, State> {
    state: State = {
        title: "ddd"
    };

    onChange = () : void => {
        setInterval(() => {
            if(index === HOMETEXT_len){
                TEXTSTATE = false;
            }
            else if(index === -1){
                TEXTSTATE = true;
                index++;
            }

            if(TEXTSTATE){
                home_text += HOMETEXT[index++];
            }
            else{
                home_text = home_text.substr(0, --index);
            }   
            this.setState({title : home_text});
        }, time)

    }

    render() {
        return (
            <div id="home">
                <span>{this.onChange}</span>
                <span>{this.state.title}</span>
                <button onClick={this.onChange}>button</button>
            </div>
        )
    }
}

export default Home;
