    headerBtn[0].setAttribute('aria-selected','true');
    console.log(activeClassUnder[0].getAttribute('style'));
  }
  else if(currentScroll < 1300) {
    //left:480px;
    if(activeClass.length){
      activeClass[0].setAttribute('aria-selected','false');
      activeClass[0].classList.remove('Mui-selected');
    }
    headerBtn[1].classList.add('Mui-selected');
    headerBtn[1].setAttribute('aria-selected','true');
  }
  else if(currentScroll < 2000) {
    //left:640px;
  }
  else if(currentScroll < 3200) {
    //left:800px;
  }
  else {
    //left:960px;
  }
})

const App: React.FC = () => {
  return (
    <div className="App">
      <Header />
      <Home />
      <About />
      <Skills />
      <Projects />
      <Contact />
    </div>
  );
};

export default App;
