import React from "react";
import "./App.scss";
import Header from "./components/Header";
import Home from "./components/Home";
import About from "./components/About";
import Skills from "./components/Skills";
import Projects from "./components/Projects";
import Contact from "./components/Contact";

const headerBtnRemoveClass = (headerBtn: any) => {
  headerBtn.classList.add("Mui-selected");
  headerBtn.setAttribute("aria-selected", "true");
};

const headerBtnAddClass = (headerBtn: any) => {
  headerBtn.classList.add("Mui-selected");
  headerBtn.setAttribute("aria-selected", "true");
};
// 스크롤바 이벤트
document.addEventListener("scroll", () => {
  const currentScroll = window.scrollY;
  const headerBtn = document.querySelectorAll(".MuiButtonBase-root");
  var activeClass = document.getElementsByClassName("Mui-selected");

  if (currentScroll < 350) {
    if (activeClass.length) {
      activeClass[0].setAttribute("aria-selected", "false");
      activeClass[0].classList.remove("Mui-selected");
    }
    headerBtnEvent(headerBtn[0]);
  } else if (currentScroll < 1300) {
    if (activeClass.length) {
      activeClass[0].setAttribute("aria-selected", "false");
      activeClass[0].classList.remove("Mui-selected");
    }
    headerBtnEvent(headerBtn[1]);
  } else if (currentScroll < 2000) {
    if (activeClass.length) {
      activeClass[0].setAttribute("aria-selected", "false");
      activeClass[0].classList.remove("Mui-selected");
    }
    headerBtnEvent(headerBtn[2]);
  } else if (currentScroll < 3200) {
    if (activeClass.length) {
      activeClass[0].setAttribute("aria-selected", "false");
      activeClass[0].classList.remove("Mui-selected");
    }
    headerBtnEvent(headerBtn[3]);
  } else {
    if (activeClass.length) {
      activeClass[0].setAttribute("aria-selected", "false");
      activeClass[0].classList.remove("Mui-selected");
    }
    headerBtnEvent(headerBtn[4]);
  }
});

const App: React.FC = () => {
  return (
    <div className="App">
      <Header />
      <Home />
      <About />
      <Skills />
      <Projects />
      <Contact />
    </div>
  );
};

export default App;
