import React, { useState, useEffect } from 'react';
import '../css/Home.css';


function changeTitle() {
    const [homeTitle] = useState('   Hello, I’m a JavaScript Developer.     ');
    const [title, setTitle] = useState('');
    const [index, setIndex] = useState(0);
    const [state, setState] = useState(true);

    setInterval(() => {
        if(index === homeTitle.length){
            setState(false);
        }
        else if(index === -1){
            setState(true);
        }

        if(state){
            setIndex(index + 1);
            setTitle(title + homeTitle[index]);
        }
        else{
            setIndex(index - 1);
            setTitle(title.substr(0, index));
        }   
    }, 1000)
}

function Home() {
