import React from "react";
import "./App.scss";
import Header from "./components/Header";
import Home from "./components/Home";
import About from "./components/About";
import Skills from "./components/Skills";
import Projects from "./components/Projects";
import Contact from "./components/Contact";

// 스크롤바 이벤트
document.addEventListener('scroll', () => {
  const currentScroll = window.scrollY;
  const headerBtn = document.querySelectorAll(".MuiButtonBase-root");
  console.log(headerBtn);

  // Mui-selected
  // aria-selected="true"
  if(currentScroll < 350) {
    console.log("home");
    // window.location.href="#home";
    //left:320px;
    for(let i = 0; i < headerBtn.length; i++){
      console.log(i, headerBtn[i].getElementsByClassName("Mui-selected"));
      // if(headerBtn[i].getElementsByClassName("Mui-selected")){
      //   headerBtn[i].get
      // }
    }
  }
  else if(currentScroll < 1300) {
    console.log("about");
    // window.location.href="#about";
    //left:480px;
  }
  else if(currentScroll < 2000) {
    console.log("skills");
    //left:640px;
  }
  else if(currentScroll < 3200) {
    console.log("projects");
    //left:800px;
  }
  else {
    console.log("contact");
    //left:960px;
  }
})

const App: React.FC = () => {
  return (
    <div className="App">
      <Header />
      <Home />
      <About />
      <Skills />
      <Projects />
      <Contact />
    </div>
  );
};

export default App;
