    constructor(text : string){
        super(text);
        this.text = text;
    }

    onText() {
        setInterval(() => {
            if(index === HOMETEXT_len){
                TEXTSTATE = false;
            }
            else if(index === -1){
                TEXTSTATE = true;
                index++;
            }

            if(TEXTSTATE){
                this.setState(this.text += HOMETEXT[index++]);
            }
            else{
                this.setState(this.text = home_text.substr(0, --index));
            }
            return home_text;
        }, 300)
    }

    render() {
        return (
            <div id="home">
                <span>{this.state}</span>
            </div>
        )
    }
}

export default Home;
