import React, { Component } from 'react';
import '../css/Home.css';

const HOMETEXT = "Hello, I’m a JavaScript Developer.";
let HOMETEXT_len = HOMETEXT.length;
let index = 0;
let TEXTSTATE = true;
let home_text = "";

const HometextDynamic =  setInterval(() => {
    if(index === HOMETEXT_len){
        TEXTSTATE = false;
    }
    else if(index === -1){
        TEXTSTATE = true;
        index++;
    }

    if(TEXTSTATE){
        home_text += HOMETEXT[index++];
    }
    else{
        home_text = home_text.substr(0, --index);
    }
    return home_text;
}, 300)

class Home extends Component {
    private text : string;

    constructor(text : string){
        super(text);
        this.text = text;
    }

    onText() {
        setInterval(() => {
            if(index === HOMETEXT_len){
                TEXTSTATE = false;
            }
            else if(index === -1){
                TEXTSTATE = true;
                index++;
            }

            if(TEXTSTATE){
                this.setState(this.text += HOMETEXT[index++]);
            }
            else{
                home_text = home_text.substr(0, --index);
            }
            return home_text;
        }, 300)
    }

    render() {
        return (
            <div id="home">
                <span>{this.state}</span>
            </div>
        )
    }
}

export default Home;
