import React, { useState, useEffect } from 'react';
import '../css/Home.css';

function Home() {
    const HOMETEXT = '   Hello, I’m a JavaScript Developer.     ';
    let HOMETEXT_len = HOMETEXT.length;
    let index = 0;
    let TEXTSTATE = true;
    let home_text = '';
    const [title, setTitle] = useState('');

    useEffect(() => {

    });

    const onChangeTitle = () : void => {
        setInterval(() => {
            if(index === HOMETEXT_len){
                TEXTSTATE = false;
            }
            else if(index === -1){
                TEXTSTATE = true;
                index++;
            }

            if(TEXTSTATE){
                home_text += HOMETEXT[index++];
            }
            else{
                home_text = home_text.substr(0, --index);
            }   
            setTitle(home_text);
        }, 100)
    }

    return (
        <div id="home">
            <span onChange={onChangeTitle}>{title}</span>
            <div><button>button</button></div>

        </div>
    ) 
}

