import React from "react";
import "./App.scss";
import Header from "./components/Header";
import Home from "./components/Home";
import About from "./components/About";
import Skills from "./components/Skills";
import Projects from "./components/Projects";
import Contact from "./components/Contact";

function headerBtnRemoveClass(cls: any) {
  cls.setAttribute("aria-selected", "false");
  cls.classList.remove("Mui-selected");
}

function headerBtnAddClass(headerBtn: any) {
  headerBtn.classList.add("Mui-selected");
  headerBtn.setAttribute("aria-selected", "true");
}
// 스크롤바 이벤트
document.addEventListener("scroll", () => {
  const currentScroll = window.scrollY;
  const headerBtn = document.querySelectorAll(".MuiButtonBase-root");
  let activeClass = document.getElementsByClassName("Mui-selected");

  if (currentScroll < 350) {
    if (activeClass.length) {
      headerBtnRemoveClass(activeClass[0]);
    }
    headerBtnAddClass(headerBtn[0]);
  } else if (currentScroll < 1300) {
    if (activeClass.length) {
      headerBtnRemoveClass(activeClass[1]);
    }
    headerBtnAddClass(headerBtn[1]);
  } else if (currentScroll < 2000) {
    if (activeClass.length) {
      headerBtnRemoveClass(activeClass[2]);
    }
    headerBtnAddClass(headerBtn[2]);
  } else if (currentScroll < 3200) {
    if (activeClass.length) {
      headerBtnRemoveClass(activeClass[3]);
    }
    headerBtnAddClass(headerBtn[3]);
  } else {
    if (activeClass.length) {
      headerBtnRemoveClass(activeClass[4]);
    }
    headerBtnAddClass(headerBtn[4]);
  }
});

const App: React.FC = () => {
  return (
    <div className="App">
      <Header />
      <Home />
      <About />
      <Skills />
      <Projects />
      <Contact />
    </div>
  );
};

export default App;
