import React, { Component } from 'react';
import '../css/Home.css';

const HOMETEXT = "Hello, I’m a JavaScript Developer.";
let text = "";
let text_len = HOMETEXT.length;
let index = 0;
let TEXTSTATE = true;

const textRun = () => {
    alert("dd");
    while(true){
        if(TEXTSTATE){
            setInterval(() => {textOn();}, 2000)
        }
        else{
            textOff();
        }
    }
}

setInterval(() => {
    if(index === text_len){
        TEXTSTATE = false;
        return;
    }
    else if(index === -1){
        TEXTSTATE = true;
        index++;
    }

    text += HOMETEXT[++index];
}, 2000)

const textOn = () => {
    setInterval(() => {
        if(index === text_len){
            TEXTSTATE = false;
            return;
        }
        text += HOMETEXT[++index];
    }, 2000)
}

const textOff = () => {
    setInterval(() => {
        if(index === -1){
            TEXTSTATE = true;
            index++;
        }
        text.substr(--index, 1);
    }, 2000)
}


class Home extends Component {
    render() {
        return (
            <div id="home" onClick={textRun}>
                <span>Hello, I’m a JavaScript Developer.</span>
            </div>
        )
    }
}

export default Home;
