class Home extends Component {

    constructor(props : any){
        super(props);
        this.state = "Hello, I’m a JavaScript Developer.";
        this.changeTitle = this.changeTitle.bind(this);
    }

    changeTitle() {
        this.setState({title : "React"});
        const HOMETEXT = "Hello, I’m a JavaScript Developer.";
        let HOMETEXT_len = HOMETEXT.length;
        let index = 0;
        let TEXTSTATE = true;
        let home_text = "";

        setInterval(() => {
            if(index === HOMETEXT_len){
                TEXTSTATE = false;
            }
            else if(index === -1){
                TEXTSTATE = true;
                index++;
            }

            if(TEXTSTATE){
                this.setState({title : HOMETEXT[index++]});
            }
            else{
                this.setState({title : home_text.substr(0, --index)});
            }

            console.log(this.state.title);
        }, 300)
    }


    render() {
        return (
            <div id="home">
                <span>{this.changeTitle}</span>
            </div>
        )
    }
}

export default Home;
